import '../App.css';
import pmc from './profile.module.css';
import React from 'react';

const Profile = props => {
  console.log(props.stats);
  return (
    <div className={pmc.profile}>
      <div className={pmc.description}>
        <img src={props.avatar} alt="User avatar" className={pmc.avatar} />
        <p className={pmc.name}>{props.username}</p>
        <p className={pmc.tag}>@{props.tag}</p>
        <p className={pmc.location}>{props.location}</p>
      </div>

      <ul className={pmc.stats}>
        <li>
          <span className={pmc.label}>Followers</span>
          <span className={pmc.quantity}>{props.stats.followers}</span>
        </li>
        <li>
          <span className={pmc.label}>Views</span>
          <span className={pmc.quantity}>{props.stats.views}</span>
        </li>
        <li>
          <span className={pmc.label}>Likes</span>
          <span className={pmc.quantity}>{props.stats.likes}</span>
        </li>
      </ul>
    </div>
  );
};

export default Profile;
